const squareSize = eventsFunctionContext.getArgument("squareSize");
const angle = eventsFunctionContext.getArgument("angle");

const a = Math.asin(Math.tan(angle * Math.PI / 180));
eventsFunctionContext.returnValue = Math.cos(a) * squareSize;

// Take first slope only because slopes don't add up.
const behavior = eventsFunctionContext.getObjects("Object")[0].getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));
const followers = eventsFunctionContext.getObjects("Follower");
for (var i = 0; i < followers.length; i++) {
    const follower = eventsFunctionContext.getObjects("Follower")[i];
    const deltaScreenX = eventsFunctionContext.getArgument("DeltaX");
    const deltaScreenY = eventsFunctionContext.getArgument("DeltaY");

    const isometry = behavior._behaviorData.IsometryAngle * Math.PI / 180;
    // Take the opposite to avoid to always set negative angles
    const inclinationAngle = - behavior._behaviorData.InclinationAngle * Math.PI / 180;
    const directionAngle = behavior._behaviorData.DirectionAngle * Math.PI / 180;

    const a = Math.asin(Math.tan(isometry));

    // Transformation in the isometric basis
    const deltaWorldX = (deltaScreenX + deltaScreenY / Math.sin(a)) * Math.sqrt(2) / 2;
    const deltaWorldY = (-deltaScreenX + deltaScreenY / Math.sin(a)) * Math.sqrt(2) / 2;

    // Unit vector of the rotation axis for inclination
    // (rotation of the vector (0; 1) by directionAngle)
    const uX = -Math.sin(directionAngle);
    const uY = Math.cos(directionAngle);

    const cosR = Math.cos(inclinationAngle);
    const sinR = Math.sin(inclinationAngle);

    // https://en.wikipedia.org/wiki/Rotation_matrix#Rotation_matrix_from_axis_and_angle
    const rotatedDeltaWorldX = (cosR+uX*uX*(1-cosR)) * deltaWorldX + (uX*uY*(1-cosR)) * deltaWorldY;
    const rotatedDeltaWorldY = (uY*uX*(1-cosR)) * deltaWorldX + (cosR+uY*uY*(1-cosR)) * deltaWorldY;
    const rotatedDeltaWorldZ = -uY*sinR * deltaWorldX + uX*sinR * deltaWorldY;

    // Transformasion in the screen basis
    const rotatedDeltaScreenX = (rotatedDeltaWorldX - rotatedDeltaWorldY) * Math.sqrt(2) / 2;
    const rotatedDeltaScreenY = (rotatedDeltaWorldX + rotatedDeltaWorldY) * Math.sin(a) * Math.sqrt(2) / 2 - Math.cos(a) * rotatedDeltaWorldZ;

    follower.setPosition(
        follower.getX() - deltaScreenX + rotatedDeltaScreenX,
        follower.getY() - deltaScreenY + rotatedDeltaScreenY);
}
